// ============================================
// EMAIL TRACKING INJECTOR
// Adds open tracking pixel and click tracking
// ============================================

const DASHBOARD_URL = 'https://cold-email-dashboard.vercel.app';

// Get email data from previous nodes
const emailBody = $json.email_1_body || $json.email_2_body || $json.email_3_body || $json.body || '';
const contactEmail = $json.email_address || $json.contact_email || '';
const campaign = $json.state || $json.campaign || 'Ohio';
const step = $json.email_number || $json.step || 1;

// Generate unique token for deduplication
const token = `${contactEmail}-${step}-${Date.now()}`;
const encodedToken = encodeURIComponent(token);

// Build tracking pixel URL
const trackingPixelUrl = `${DASHBOARD_URL}/api/track/open?e=${encodeURIComponent(contactEmail)}&c=${encodeURIComponent(campaign)}&s=${step}&t=${encodedToken}`;

// Build click tracking wrapper function (without URLSearchParams)
function wrapLink(originalUrl, linkId) {
  const encodedUrl = encodeURIComponent(originalUrl);
  const encodedEmail = encodeURIComponent(contactEmail);
  const encodedCampaign = encodeURIComponent(campaign);
  const encodedLinkId = encodeURIComponent(linkId || 'cta');
  return `${DASHBOARD_URL}/api/track/click?url=${encodedUrl}&e=${encodedEmail}&c=${encodedCampaign}&s=${step}&l=${encodedLinkId}`;
}

// Process email body
let trackedBody = emailBody;

// Wrap all <a href="..."> links with click tracking
const linkRegex = /<a\s+([^>]*?)href=["']([^"']+)["']([^>]*?)>/gi;
let linkIndex = 0;
trackedBody = trackedBody.replace(linkRegex, (match, before, url, after) => {
  // Skip mailto: and tel: links
  if (url.startsWith('mailto:') || url.startsWith('tel:') || url.startsWith('#')) {
    return match;
  }
  linkIndex++;
  const trackedUrl = wrapLink(url, `link_${linkIndex}`);
  return `<a ${before}href="${trackedUrl}"${after}>`;
});

// Add tracking pixel at the end of the email
const trackingPixel = `<img src="${trackingPixelUrl}" width="1" height="1" style="display:none;width:1px;height:1px;border:0;" alt="" />`;

// If email is HTML, add before closing body tag or at end
if (trackedBody.includes('</body>')) {
  trackedBody = trackedBody.replace('</body>', `${trackingPixel}</body>`);
} else if (trackedBody.includes('</html>')) {
  trackedBody = trackedBody.replace('</html>', `${trackingPixel}</html>`);
} else {
  // Plain text or simple HTML - append at end
  trackedBody = `${trackedBody}\n${trackingPixel}`;
}

// Return modified item with tracked body
return [
  {
    json: {
      ...$json,
      tracked_body: trackedBody,
      original_body: emailBody,
      tracking_pixel_url: trackingPixelUrl,
      links_tracked: linkIndex,
      tracking_token: encodedToken
    }
  }
];
